import { useState } from 'react';
import Button from './Button';
import Board from './Board';
import './App.css'
import logo from './assets/logo.png'




function random(n) {
  return Math.ceil(Math.random() * n);
}

function App() {


  const [myHistory, setMyHistory] = useState([]); 
  const [otherHistory, setOtherHistory] = useState([]); 


  const handleRollClick = () => {
    const nextMyNum = random(6);
    const nextOtherNum = random(6);
    setMyHistory([...myHistory, nextMyNum]);
    setOtherHistory([...otherHistory, nextOtherNum]);

    let myClassName = 'Board';
    let otherClassName = 'Board';
    if (myHistory[myHistory.length - 1] > otherHistory[otherHistory.length - 1]) {
      myClassName += ' Board-winner';
    } else if (myHistory[myHistory.length - 1] < otherHistory[otherHistory.length - 1]) {
      otherClassName += ' Board-winner';
    }

  };

  const handleClearClick = () => {
    setMyHistory([]);
    setOtherHistory([]);
  };



  return(
    <div className="App">
      <img class="App-logo" src={logo} alt="주사위게임 로고" />
      <h1 class="App-title">주사위게임</h1>

    <Button className="Button blue App-button" onClick={handleRollClick}>던지기</Button>
    <Button className="Button red App-button" onClick={handleClearClick}>처음부터</Button>


    <div className="App-boards">
      <Board 
      className="myClassName"
      color="blue"
      name="나" 
      gameHistory={myHistory}


      />
      <Board  
      className="otherClassName"  
      color="red" 
      name="상대" 
      gameHistory={otherHistory}/>
    </div>
    </div>
  );
}

export default App;
