import { useState } from 'react';
import Button from './Button';
import Dice from './Dice';
import Board from './Board';

function random(n) {
  return Math.ceil(Math.random() * n);
}

function App() {
  const [num, setNum] = useState(1); //주사위 숫자
  const [sum, setSum] = useState(0); //총점
  const [gameHistory, setGameHistory] = useState([]); //나온 주사위 숫자 순서대로 나옴
  const [otherNum, setOtherNum] = useState(1); //주사위 숫자
  const [otherSum, setOtherSum] = useState(0); //총점
  const [otherGameHistory, setOtherGameHistory] = useState([]); //나온 주사위 숫자 순서대로 나옴

  const handleRollClick = () => {
    const nextNum = random(6);
    const nextOtherNum = random(6);
    setNum(nextNum);
    setSum(sum + nextNum);
    setGameHistory([...gameHistory, nextNum]);
    setOtherNum(nextOtherNum);
    setOtherSum(otherSum + nextOtherNum);
    setOtherGameHistory([...otherGameHistory, nextOtherNum]);
  };

  const handleClearClick = () => {
    setNum(1);
    setSum(0);
    setGameHistory([]);
    setOtherNum(1);
    setOtherSum(0);
    setOtherGameHistory([]);
  };



  return(
    <div>
    <div>
    <Button onClick={handleRollClick}>던지기</Button>
    <Button onClick={handleClearClick}>처음부터</Button>
    </div>

    <div>
      <Board color="blue" name="나" num={num} sum={sum} gameHistory={gameHistory}/>
      <Board color="red" name="상대"  num={otherNum} sum={otherSum} gameHistory={otherGameHistory}/>
    </div>
    </div>
  );
}

export default App;
